mapper.insert:
INSERT INTO user_profile (
    user_id, real_name, gender, birthday, avatar,
    deleted, version, created_at, updated_at, created_by, updated_by
) VALUES (
             #{userId}, #{realName}, #{gender}, #{birthday}, #{avatar},
             #{deleted}, #{version}, #{createdAt}, #{updatedAt}, #{createdBy}, #{updatedBy}
         )

mapper.update:
UPDATE user_profile
real_name = #{realName}, gender = #{gender}, birthday = #{birthday}, avatar = #{avatar}, deleted = #{deleted}, version = version + 1, updated_at = #{updatedAt}, updated_by = #{updatedBy}
WHERE user_id = #{userId} AND version = #{version}
